class Solution(object):
    def findTheDifference(self, s, t):
        """
        :type s: str
        :type t: str
        :rtype: str
        """
        ind = 0      
        while ind < len(s):
            found_ind = t.index(s[ind]) # watch out for this lil bitch -> index(): find the first index of multiple same characters
            t = t[:found_ind] + t[found_ind+1:]
            ind += 1
        return t

if __name__ == '__main__':
    s = 'abcd'
    t = 'abcde'
#    s = ''
#    t = ''

    sol = Solution()
    o = sol.findTheDifference(s, t)
    print(o)