import time

# undone

# too slow
class Solution(object):
    def longestPalindrome(self, s):
        """
        :type s: str
        :rtype: str
        """
        ind = 0
        maxLen = 1
        
        i = 0
        while i < len(s):
            j = i+maxLen
            while i+maxLen < len(s) and j < len(s) and j - i + 1 > maxLen:
                if self.checkPalindromic(s,i,j):
                    ind = i
                    maxLen = j - i + 1
                j += 1
            i += 1
            
                    
        return s[ind:ind+maxLen]
    
    def checkPalindromic(self, s, i, j):
        init, end = i, j
        while end >= init:
            if s[init] == s[end]:
                init += 1
                end -= 1
            else:
                return False
        return True
        

